mapper.select:
SELECT c.std_name, b.std_no, b.grade, b.recruit_type, d.unvs_name, e.pfsn_code, e.pfsn_name, e.pfsn_level, g.exam_year, h.ep_name, DATE_FORMAT(f.start_time, '%Y-%m-%d %p %H:%i') start_time, DATE_FORMAT(f.end_time, '%H:%i') end_time, i.place_name, a.es_num, j.eyp_code, k.sign_status FROM bd_exam_seats a LEFT JOIN bd_learn_info b ON a.learn_id = b.learn_id LEFT JOIN bd_student_info c ON b.std_id = c.std_id LEFT JOIN bd_university d ON b.unvs_id = d.unvs_id LEFT JOIN bd_unvs_profession e ON b.pfsn_id = e.pfsn_id LEFT JOIN bd_place_year f ON a.py_id = f.py_id LEFT JOIN bd_exam_year g ON g.ey_id = f.ey_id LEFT JOIN bd_exam_place h ON f.place_id = h.ep_id LEFT JOIN bd_place_info i ON a.place_id = i.place_id LEFT JOIN bd_exam_year_profession j ON j.ey_id = g.ey_id AND j.grade = b.grade AND j.unvs_id = b.unvs_id AND j.pfsn_level = e.pfsn_level AND j.pfsn_id = b.pfsn_id LEFT JOIN bd_student_exam_affirm k ON a.learn_id = k.learn_id and a.py_id = k.py_id and g.ey_id = k.ey_id WHERE 1=1
AND c.std_name LIKE CONCAT('%',#{stdName,jdbcType=VARCHAR} , '%')
and (j.eyp_code is null or j.eyp_code = '')
and LENGTH(j.eyp_code) > 0
AND c.id_card LIKE CONCAT('%',#{idCard,jdbcType=VARCHAR} , '%')
AND c.mobile LIKE CONCAT('%',#{mobile,jdbcType=VARCHAR} , '%')
AND b.unvs_id = #{unvsId,jdbcType=VARCHAR}
AND e.pfsn_id= #{pfsnId,jdbcType=VARCHAR}
AND e.pfsn_level = #{pfsnLevel,jdbcType=VARCHAR}
AND b.grade = #{grade,jdbcType=VARCHAR}
AND f.place_id = #{placeId,jdbcType=VARCHAR}
AND g.ey_id = #{eyId,jdbcType=VARCHAR}
AND DATE_FORMAT(f.start_time, '%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(f.end_time, '%Y-%m-%d') <= #{endTime}
AND b.recruit_type = #{recruitType,jdbcType=VARCHAR}
order by a.place_Id desc,a.es_Num desc

SELECT a.learn_id, h.es_num, g.std_name, b.school_roll std_no, b.grade, c.unvs_name, CASE d.pfsn_level WHEN 1 THEN '本科' WHEN 5 THEN '专科' END pfsn_level, d.pfsn_name, f.eyp_code, '' sign FROM bd_student_exam_affirm a LEFT JOIN bd_learn_info b ON a.learn_id = b.learn_id LEFT JOIN bd_university c ON b.unvs_id = c.unvs_id LEFT JOIN bd_unvs_profession d ON b.pfsn_id = d.pfsn_id LEFT JOIN bd_exam_year_profession f ON f.ey_id = a.ey_id AND f.grade = b.grade AND f.unvs_id = b.unvs_id AND f.pfsn_level = d.pfsn_level AND f.pfsn_id = b.pfsn_id LEFT JOIN bd_student_info g ON b.std_id = g.std_id LEFT JOIN bd_exam_seats h ON b.learn_id = h.learn_id AND a.py_id = h.py_id WHERE 1=1
AND a.py_id = #{pyId,jdbcType=VARCHAR}
AND h.place_id = #{placeId,jdbcType=VARCHAR}
ORDER BY eyp_code ASC, b.std_no ASC, es_num ASC

mapper.delete:
DELETE a FROM bd_exam_seats a
JOIN bd_place_year b
ON a.py_id = b.py_id
WHERE 1=1

    AND b.ey_id  = #{eyId,jdbcType=VARCHAR}


    AND b.place_id  = #{epId,jdbcType=VARCHAR}


    AND b.start_time  = #{startTime,jdbcType=VARCHAR}


    AND b.end_time  = #{endTime,jdbcType=VARCHAR}

mapper.select:
SELECT
b.place_id dictValue,
place_name dictName
FROM
bd_place_year a
JOIN bd_place_info b ON a.py_id = b.py_id

    AND a.ey_id  = #{eyId,jdbcType=VARCHAR}


    AND a.place_id  = #{epId,jdbcType=VARCHAR}


    AND a.start_time  = #{startTime,jdbcType=VARCHAR}


    AND a.end_time  = #{endTime,jdbcType=VARCHAR}

SELECT c.std_name, b.school_roll, (SELECT dict_name FROM sys_dict WHERE p_id = 'grade' AND dict_value = b.grade) grade, b.recruit_type, d.unvs_name, e.pfsn_code, e.pfsn_name, (SELECT dict_name FROM sys_dict WHERE p_id = 'pfsnLevel' AND dict_value = e.pfsn_level) pfsn_level, g.exam_year, h.ep_name, concat(DATE_FORMAT(f.start_time, '%Y-%m-%d %H:%i'),' - ',DATE_FORMAT(f.end_time, '%H:%i')) `time`, DATE_FORMAT(f.start_time, '%Y-%m-%d %H:%i') start_time, DATE_FORMAT(f.end_time, '%H:%i') end_time, i.place_name, a.es_num, j.eyp_code, k.sign_status, (SELECT province_name FROM sys_province WHERE province_code = h.province_code) province_name, (SELECT city_name FROM sys_city WHERE city_code = h.city_code) city_name, (SELECT district_name FROM sys_district WHERE district_code = h.district_code) district_name FROM bd_exam_seats a LEFT JOIN bd_learn_info b ON a.learn_id = b.learn_id LEFT JOIN bd_student_info c ON b.std_id = c.std_id LEFT JOIN bd_university d ON b.unvs_id = d.unvs_id LEFT JOIN bd_unvs_profession e ON b.pfsn_id = e.pfsn_id LEFT JOIN bd_place_year f ON a.py_id = f.py_id LEFT JOIN bd_exam_year g ON g.ey_id = f.ey_id LEFT JOIN bd_exam_place h ON f.place_id = h.ep_id LEFT JOIN bd_place_info i ON a.place_id = i.place_id LEFT JOIN bd_exam_year_profession j ON j.ey_id = g.ey_id AND j.grade = b.grade AND j.unvs_id = b.unvs_id AND j.pfsn_level = e.pfsn_level AND j.pfsn_id = b.pfsn_id LEFT JOIN bd_student_exam_affirm k ON a.learn_id = k.learn_id and a.py_id = k.py_id and g.ey_id = k.ey_id WHERE 1=1
AND c.std_name LIKE CONCAT('%',#{stdName,jdbcType=VARCHAR} , '%')
and (j.eyp_code is null or j.eyp_code = '')
and LENGTH(j.eyp_code) > 0
AND c.id_card LIKE CONCAT('%',#{idCard,jdbcType=VARCHAR} , '%')
AND c.mobile LIKE CONCAT('%',#{mobile,jdbcType=VARCHAR} , '%')
AND b.unvs_id = #{unvsId,jdbcType=VARCHAR}
AND e.pfsn_id= #{pfsnId,jdbcType=VARCHAR}
AND e.pfsn_level = #{pfsnLevel,jdbcType=VARCHAR}
AND b.grade = #{grade,jdbcType=VARCHAR}
AND f.place_id = #{placeId,jdbcType=VARCHAR}
AND g.ey_id = #{eyId,jdbcType=VARCHAR}
AND DATE_FORMAT(f.start_time, '%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(f.end_time, '%Y-%m-%d') <= #{endTime}
AND b.recruit_type = #{recruitType,jdbcType=VARCHAR}
order by a.place_Id desc,a.es_Num desc
