mapper.select:
SELECT oeli.`recrods_no`, oeli.`learn_id`, bsi.`std_name`, bli.`grade`, bli.`scholarship`, bli.`recruit_type`, bu.`unvs_name`, bup.`pfsn_code`, bup.`pfsn_name`, bup.`pfsn_level`, bli.`std_stage`, odo.`dp_name` old_dp_name, oco.`campus_name` old_campus_name, ogo.`group_name` old_group_name, oeli.`oper_status`, date_format(oeli.`oper_time`,'%Y-%m-%d %H:%i:%S') oper_time, oeli.`oper_user`, od.`dp_name` dp_name, oc.`campus_name` campus_name, og.`group_name` group_name FROM oa_employee_learn_info oeli LEFT JOIN bd_learn_info bli ON bli.`learn_id` = oeli.`learn_id` LEFT JOIN bd_student_info bsi ON bsi.`std_id` = bli.`std_id` LEFT JOIN bd_university bu ON bli.`unvs_id` = bu.`unvs_id` LEFT JOIN bd_unvs_profession bup ON bli.`pfsn_id` = bup.`pfsn_id` LEFT JOIN oa_campus oco ON oeli.`old_campus_id` = oco.`campus_id` LEFT JOIN oa_department odo ON oeli.`old_dp_id` = odo.`dp_id` LEFT JOIN oa_group ogo on ogo.`group_id` = oeli.`old_group_Id` LEFT JOIN oa_campus oc ON oeli.`campus_id` = oc.`campus_id` LEFT JOIN oa_department od ON oeli.`dp_id` = od.`dp_id` LEFT JOIN oa_group og ON og.`group_id` = oeli.`group_id` WHERE 1=1
AND oeli.modify_id = #{modifyId,jdbcType=VARCHAR}
AND oeli.`old_emp_id` =#{empId,jdbcType=VARCHAR}
AND oeli.`old_dp_id` =#{dpId,jdbcType=VARCHAR}
AND bsi.`std_name` like CONCAT('%', CONCAT(#{stdName}, '%'))
AND bsi.`mobile` = #{stdPhone,jdbcType=VARCHAR}
AND bsi.`id_card` = #{stdIdCard,jdbcType=VARCHAR}
AND bli.`recruit_type` = #{recruitType,jdbcType=VARCHAR}
AND bu.`unvs_name` like CONCAT('%', CONCAT(#{stdUnvsName}, '%'))
AND bup.`pfsn_name` like CONCAT('%', CONCAT(#{stdPfsnName}, '%'))
AND bli.`std_stage` = #{stdStage,jdbcType=VARCHAR}
AND bli.`scholarship` = #{scholarship,jdbcType=VARCHAR}
AND bli.`grade` = #{stdGrade,jdbcType=VARCHAR}
AND oeli.`oper_status` = #{operStatus,jdbcType=VARCHAR}
and DATE_FORMAT(bli.`create_time`, '%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(bli.`create_time`, '%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')

mapper.update:
UPDATE bd_learn_rules SET 
	recruit_campus_id=#{modifyInfo.newCampusId,jdbcType=VARCHAR},
	recruit_dp_id=#{modifyInfo.newDpId,jdbcType=VARCHAR},
	recruit_group_id=#{modifyInfo.newGroupId,jdbcType=VARCHAR}
WHERE learn_id IN 

	#{item.learnId, jdbcType=VARCHAR}

   UPDATE bd_learn_rules SET 
    recruit_campus_id=#{modifyInfo.newCampusId,jdbcType=VARCHAR},
	recruit_dp_id=#{modifyInfo.newDpId,jdbcType=VARCHAR},
	recruit_group_id=#{modifyInfo.newGroupId,jdbcType=VARCHAR} 
   WHERE recruit=#{modifyInfo.empId,jdbcType=VARCHAR}
	and recruit_dp_id = #{modifyInfo.oldDpId,jdbcType=VARCHAR}

UPDATE bd_learn_rules SET 
	recruit_campus_id=#{modifyInfo.oldCampusId,jdbcType=VARCHAR},
	recruit_dp_id=#{modifyInfo.oldDpId,jdbcType=VARCHAR},
	recruit_group_id=#{modifyInfo.oldGroupId,jdbcType=VARCHAR}
WHERE learn_id IN 

	#{item.learnId, jdbcType=VARCHAR}

   UPDATE bd_learn_rules SET 
    recruit_campus_id=#{modifyInfo.oldCampusId,jdbcType=VARCHAR},
	recruit_dp_id=#{modifyInfo.oldDpId,jdbcType=VARCHAR},
	recruit_group_id=#{modifyInfo.oldGroupId,jdbcType=VARCHAR} 
   WHERE recruit=#{modifyInfo.empId,jdbcType=VARCHAR}
	and recruit_dp_id = #{modifyInfo.oldDpId,jdbcType=VARCHAR}

UPDATE bd_learn_rules SET 
	recruit_campus_id=#{modifyInfo.newCampusId,jdbcType=VARCHAR},
	recruit_dp_id=#{modifyInfo.newDpId,jdbcType=VARCHAR},
	recruit_group_id=#{modifyInfo.newGroupId,jdbcType=VARCHAR}
WHERE learn_id IN 

	#{item.learnId, jdbcType=VARCHAR}
