mapper.select:
SELECT a.modify_id, a.std_name, a.id_card, a.sex, a.nation, a.pfsn_id, b.grade, a.check_order, b.std_stage, b.scholarship, b.inclusion_status, a.new_std_name, a.new_id_card, a.new_sex, a.new_nation, a.new_pfsn_id, pro.pfsn_name, pro_new.pfsn_name npfsn_name, pro.pfsn_code, pro.pfsn_level, a.create_user, a.create_time, c.unvs_name, d.check_status, d.cr_id, d.update_time, unvs_new.unvs_name nunvs_name, pro_new.pfsn_level new_pfsn_level, bta.ta_name, bta_new.ta_name nta_name FROM bd_student_modify a LEFT JOIN bd_learn_info b ON a.learn_id = b.learn_id LEFT JOIN bd_university c ON a.unvs_id = c.unvs_id LEFT JOIN bd_university unvs_new ON a.new_unvs_id = unvs_new.unvs_id LEFT JOIN bd_unvs_profession pro ON a.pfsn_id = pro.pfsn_id LEFT JOIN bd_unvs_profession pro_new ON a.new_pfsn_id = pro_new.pfsn_id LEFT JOIN bd_check_record d ON d.mapping_id = a.modify_id AND d.check_order = a.check_order LEFT JOIN bd_learn_rules e ON a.learn_id = e.learn_id LEFT JOIN bd_student_info f ON f.std_id = b.std_id LEFT JOIN bd_test_area bta ON bta.`ta_id` = a.`ta_id` LEFT JOIN bd_test_area bta_new ON bta_new.`ta_id` = a.`new_ta_id` WHERE a.modify_type = '6' AND d.check_type = '10'
AND a.`std_name` LIKE CONCAT('%', #{query.stdName,jdbcType=VARCHAR}, '%')
AND a.`id_card` LIKE CONCAT('%', #{query.idCard,jdbcType=VARCHAR}, '%')
AND f.`mobile` LIKE CONCAT('%', #{query.mobile,jdbcType=VARCHAR}, '%')
AND a.`unvs_id` = #{query.unvsId,jdbcType=VARCHAR}
AND pro.`pfsn_id` = #{query.pfsnId ,jdbcType=VARCHAR}
AND pro.`pfsn_level` = #{query.pfsnLevel,jdbcType=VARCHAR}
AND b.`grade` = #{query.grade,jdbcType=VARCHAR}
AND a.create_user LIKE CONCAT('%', #{query.createUser,jdbcType=VARCHAR}, '%')
AND b.`recruit_type` = #{query.recruitType,jdbcType=VARCHAR}
AND b.`std_stage` = #{query.stdStage,jdbcType=VARCHAR}
AND a.`create_time` >= #{query.createStartTime,jdbcType=VARCHAR}
AND a.`create_time` <= #{query.createEndTime,jdbcType=VARCHAR}
AND (a.check_order=3 AND d.check_status=2 AND d.`update_time` >= #{query.checkStartTime,jdbcType=VARCHAR})
AND (a.check_order=3 AND d.check_status=2 AND d.`update_time` <= #{query.checkEndTime,jdbcType=VARCHAR})
AND a.`check_order` = #{query.checkOrder,jdbcType=VARCHAR} AND d.check_status ='1'
AND (a.ext_3 = #{query.changeType} or a.ext_3 = '3')
and d.check_status = 3
and a.check_order = 1 and d.check_status = 1
and a.check_order = 2 and d.check_status = 1
and a.check_order = 3 and d.check_status = 1
and a.check_order = 3 and d.check_status = 2
and e.tutor = #{user.empId, jdbcType=VARCHAR}
and a.create_user_id = #{user.userId, jdbcType=VARCHAR}
order by create_time desc

