mapper.select:
select p.pfsn_id as pfsnId, p.pfsn_name as pfsnName from bms.bd_unvs_profession p
p.is_allow = '1'
and p.unvs_id = #{unvsId, jdbcType=VARCHAR}
and p.pfsn_name LIKE CONCAT('%', #{pfsnName, jdbcType=VARCHAR}, '%')
and p.pfsn_level = #{pfsnLevel, jdbcType=VARCHAR}
and p.grade = #{grade, jdbcType=VARCHAR}
and p.pfsn_id IN (SELECT pt.pfsn_id FROM bms.bd_proferssion_ta pt LEFT JOIN bms.bd_test_area t ON t.`ta_id` = pt.`ta_id` WHERE t.`city_code` = #{cityCode, jdbcType=VARCHAR})
order by p.pfsn_name

select a.`ta_id` as taId, a.`ta_name` as taName from bms.bd_test_area a
left join bms.bd_proferssion_ta pt on pt.`ta_id` = a.`ta_id`
left join bms.bd_university_test ut on ut.`ta_id` = a.`ta_id`
a.is_allow = '1'
and ut.unvs_id = #{unvsId, jdbcType=VARCHAR}
and pt.pfsn_id = #{pfsnId, jdbcType=VARCHAR}
and a.ta_name LIKE CONCAT('%', #{taName, jdbcType=VARCHAR}, '%')
and a.`city_code` = #{cityCode, jdbcType=VARCHAR}
group by a.`ta_id` order by a.`ta_name`

select a.`ta_id` as taId, a.`ta_name` as taName from bms.bd_test_area a
left join bms.bd_proferssion_ta pt on pt.`ta_id` = a.`ta_id`
left join bms.bd_university_test ut on ut.`ta_id` = a.`ta_id`
a.is_allow = '1' and a.is_stop = '0'
and ut.unvs_id = #{unvsId, jdbcType=VARCHAR}
and pt.pfsn_id = #{pfsnId, jdbcType=VARCHAR}
and a.ta_name LIKE CONCAT('%', #{taName, jdbcType=VARCHAR}, '%')
and a.`city_code` = #{cityCode, jdbcType=VARCHAR}
group by a.`ta_id` order by a.`ta_name`
