SELECT DATE_FORMAT(bc.`start_time`, '%H:%i') startTime, DATE_FORMAT(bc.`end_time`, '%H:%i') endTime, bco.`course_name` courseName , bc.`cp_type` cpType, o.`address` FROM bms.bd_class_plan bc left join bms.bd_course bco on bco.`course_id` = bc.`course_id` left join bms.oa_campus o on o.`campus_id` = bc.`place_id` WHERE bc.`cp_type`!='3' and bc.`cp_type`!='4' and bc.`cp_id` in
#{cpId}
and bc.`cp_date` = #{date} order by DATE_FORMAT(bc.`start_time`, '%Y-%m-%d %H:%i:%s')

SELECT bc.`cp_date` FROM bms.bd_class_plan bc WHERE bc.`cp_type`!='3' and bc.`cp_type`!='4' and bc.`cp_id` IN
#{cpId}
GROUP BY bc.`cp_date` order by bc.`cp_date`

SELECT oc.`address` FROM bms.bd_class_plan bc LEFT JOIN bms.oa_campus oc ON oc.`campus_id` = bc.`place_id` WHERE bc.`cp_id` IN
#{cpId}
GROUP BY oc.`campus_id`

SELECT bc.`cp_type` FROM bms.bd_class_plan bc left join bms.bd_course bco on bco.`course_id` = bc.`course_id` WHERE bc.`cp_id` in
#{cpId}
group by bc.`cp_type`

SELECT bco.`course_name` courseName, bc.`cp_type` cpType, oc.`address` FROM bms.bd_class_plan bc LEFT JOIN bms.bd_course bco ON bco.`course_id` = bc.`course_id` LEFT JOIN bms.oa_campus oc ON oc.`campus_id` = bc.`place_id` WHERE bc.`cp_type` in ('3','4') and bc.`cp_id` IN
#{cpId}
GROUP BY bco.`course_name`, oc.`address`, bc.`cp_type`

