from bms.bd_course
where course_id = #{courseId,jdbcType=VARCHAR}

SELECT l.`cp_id`, l.course_id, bc.course_name, lc.channel_id, l.start_time, l.end_time, oe.emp_name FROM bms.bd_class_plan l LEFT JOIN bms.bd_live_channel lc ON l.course_id = lc.course_id LEFT JOIN bms.bd_course bc on l.course_id = bc.course_id LEFT JOIN bms.oa_employee oe ON oe.emp_id = l.emp_id WHERE l.`course_id` IN ( SELECT c.`course_id` FROM bms.bd_learn_info bli LEFT JOIN bms.bd_unvs_profession bup ON bup.`pfsn_id` = bli.`pfsn_id` LEFT JOIN bms.bd_test_group g ON bup.`group_id` = g.`group_id` LEFT JOIN bms.bd_tg t ON t.`group_id` = g.`group_id` LEFT JOIN bms.bd_test_subject b ON b.`subject_id` = t.`subject_id` LEFT JOIN bms.bd_course c ON ( c.`test_subject` = b.`subject_id` AND c.`grade` = bli.`grade` ) WHERE bli.`learn_id` = #{learnId} AND c.`course_id` IS NOT NULL AND bli.`std_stage` IN ('1','2', '3','4') GROUP BY c.`course_id` ) AND l.cp_type in('6','7')
AND date_format(l.start_time,'%Y-%m-%d') = #{date}
GROUP BY l.`cp_id` UNION ALL SELECT l.`cp_id`, l.course_id, bc.course_name, lc.channel_id, l.start_time, l.end_time, oe.emp_name FROM bms.bd_class_plan l LEFT JOIN bms.bd_live_channel lc ON l.course_id = lc.course_id LEFT JOIN bms.bd_course bc on l.course_id = bc.course_id LEFT JOIN bms.oa_employee oe ON oe.emp_id = l.emp_id WHERE l.`course_id` IN ( SELECT c.`course_id` FROM bms.bd_course c LEFT JOIN bms.bd_plan_course p ON p.`course_id` = c.`course_id` LEFT JOIN bms.bd_teach_plan t ON t.`thp_id` = p.`thp_id` LEFT JOIN bms.bd_unvs_profession f ON f.`pfsn_id` = t.`pfsn_id` LEFT JOIN bms.bd_learn_info bli ON bli.`pfsn_id` = f.`pfsn_id` WHERE t.`semester` = #{term} AND bli.`learn_id` = #{learnId} GROUP BY c.`course_id` ) AND l.cp_type in('6','7')
AND date_format(l.start_time,'%Y-%m-%d') = #{date}
GROUP BY l.`cp_id` ORDER BY `start_time`
